mapper.insert:
INSERT INTO TB_FNST_CD_INFO (
       FNST_CD
     , RPRS_FNST_CD
     , SWIFT_CD
     , FNST_NM
     , FNST_ENG_NM
     , APLCN_YMD
     , STTS_CD
     , CRT_ID
     , UPD_ID
)
VALUES (
       #{fnstCd}
     , #{rprsFnstCd}
     , #{swiftCd}
     , #{fnstNm}
     , #{fnstEngNm}
     , #{aplcnYmd}
     , #{sttsCd}
     , #{crtId}
     , #{updId}
)

mapper.update:
UPDATE TB_FNST_CD_INFO
   SET RPRS_FNST_CD = #{rprsFnstCd}
     , SWIFT_CD = #{swiftCd}
     , FNST_NM = #{fnstNm}
     , FNST_ENG_NM = #{fnstEngNm}
     , APLCN_YMD = #{aplcnYmd}
     , STTS_CD = #{sttsCd}
     , UPD_ID = #{updId}
 WHERE FNST_CD = #{fnstCd}

mapper.select:
SELECT COUNT(*)
  FROM TB_FNST_CD_INFO
 WHERE FNST_CD = #{fnstCd}

SELECT FNST_CD , RPRS_FNST_CD , SWIFT_CD , FNST_NM , FNST_ENG_NM , APLCN_YMD , STTS_CD , CRT_ID , date_format(CRT_DTTM, '%Y-%m-%d %H:%i:%s') AS CRT_DTTM , UPD_ID , date_format(UPD_DTTM, '%Y-%m-%d %H:%i:%s') AS UPD_DTTM FROM TB_FNST_CD_INFO WHERE 1=1
AND FNST_CD = #{fnstCd}
AND RPRS_FNST_CD = #{rprsFnstCd}
AND SWIFT_CD = #{swiftCd}
AND FNST_NM LIKE CONCAT('%',#{fnstNm} , '%')
AND FNST_ENG_NM LIKE CONCAT('%',#{fnstEngNm} , '%')
AND APLCN_YMD = #{aplcnYmd}
AND STTS_CD = #{sttsCd}
