mapper.insert:
INSERT INTO TB_HLDY_INF (
       CRTR_YMD
     , HLDY_NM
     , HLDY_SE_CD
     , DOW_SE_CD
     , CRT_ID
     , UPD_ID
)
VALUES (
       #{crtrYmd}
     , #{hldyNm}
     , #{hldySeCd}
     , #{dowSeCd}
     , #{crtId}
     , #{updId}
)

mapper.update:
UPDATE TB_HLDY_INF
   SET HLDY_NM = #{hldyNm}
     , HLDY_SE_CD = #{hldySeCd}
     , DOW_SE_CD = #{dowSeCd}
     , UPD_ID = #{updId}
 WHERE CRTR_YMD = #{crtrYmd}

mapper.select:
SELECT COUNT(*)
  FROM TB_HLDY_INF
 WHERE CRTR_YMD = #{crtrYmd}

SELECT CRTR_YMD , HLDY_NM , HLDY_SE_CD , DOW_SE_CD , CRT_ID , date_format(CRT_DTTM, '%Y-%m-%d %H:%i:%s') AS CRT_DTTM , UPD_ID , date_format(UPD_DTTM, '%Y-%m-%d %H:%i:%s') AS UPD_DTTM FROM TB_HLDY_INF WHERE 1=1
AND CRTR_YMD LIKE CONCAT('%',#{crtrYmd} , '%')
AND HLDY_NM LIKE CONCAT('%',#{hldyNm} , '%')
AND HLDY_SE_CD = #{hldySeCd}
AND DOW_SE_CD = #{dowSeCd}
