mapper.insert:
INSERT INTO tb_code_group (
       GRP_CD
     , GRP_NM
     , STAT
     , ORD
     , RMK
     , CRT_ID
     , UPD_ID
)
VALUES (
       #{grpCd}
     , #{grpNm}
     , #{stat}
     , 0
     , #{rmk}
     , #{crtId}
     , #{updId}
)

mapper.update:
UPDATE tb_code_group
   SET GRP_NM = #{grpNm}
     , STAT = #{stat}
     , ORD = #{ord}
     , RMK = #{rmk}
     , UPD_ID = #{updId}
 WHERE GRP_CD = #{grpCd}

mapper.select:
SELECT COUNT(*)
FROM tb_code_group
WHERE GRP_CD = #{grpCd}
