mapper.insert:
INSERT INTO tb_code (
       GRP_CD
     , CD
     , CD_NM
     , STAT
     , ORD
     , RMK
     , CRT_ID
     , UPD_ID
)
VALUES (
       #{grpCd}
     , #{cd}
     , #{cdNm}
     , #{stat}
     , #{ord}
     , #{rmk}
     , #{crtId}
     , #{updId}
)

mapper.update:
UPDATE tb_code
   SET CD = #{cd}
     , CD_NM = #{cdNm}
     , STAT = #{stat}
     , ORD = #{ord}
     , RMK = #{rmk}
     , UPD_ID = #{updId}
 WHERE GRP_CD = #{grpCd}
   AND CD = #{cd}

mapper.delete:
DELETE
  FROM tb_code
 WHERE GRP_CD = #{grpCd}
   AND CD = #{cd}

mapper.select:
SELECT A.GRP_CD , B.GRP_NM , A.CD , A.CD_NM , A.STAT , A.ORD , A.RMK , A.CRT_ID , A.UPD_ID , DATE_FORMAT(A.CRT_DTTM, '%Y-%m-%d %H:%i:%s') AS CRT_DTTM , DATE_FORMAT(A.UPD_DTTM, '%Y-%m-%d %H:%i:%s') AS UPD_DTTM FROM tb_code A JOIN tb_code_group B ON A.GRP_CD = B.GRP_CD WHERE 1 = 1
AND A.GRP_CD = #{grpCd}
AND A.CD LIKE CONCAT('%', #{cd}, '%')
AND A.CD_NM LIKE CONCAT('%', #{cdNm}, '%')
AND A.STAT = #{stat}
ORDER BY A.GRP_CD, A.ORD, A.CD

SELECT COUNT(*)
FROM TB_CODE
WHERE GRP_CD = #{grpCd}
AND CD = #{cd}
