mapper.insert:
INSERT INTO tb_word_dictionary (
       WORD_NM
     , ENG_ABBR_NM
     , ENG_NM
     , EXPLN
     , STAT
     , CRT_ID
     , UPD_ID
)
VALUES (
       #{wordNm}
      , #{engAbbrNm}
      , #{engNm}
      , #{expln}
      , #{stat}
      , #{crtId}
     , #{updId}
)

mapper.update:
UPDATE tb_word_dictionary
   SET
       UPD_ID = #{updId}

          , ENG_ABBR_NM = #{engAbbrNm}


          , ENG_NM = #{engNm}


          , EXPLN = #{expln}


          , STAT = #{stat}

 WHERE ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM tb_word_dictionary
WHERE WORD_NM = #{wordNm}
