mapper.insert:
INSERT INTO TB_FNNC_JNT_CD_INFO (
       JNT_CD
     , RPRS_FNST_CD
     , FNST_CD
     , FNST_NM
     , STOR_NM
     , TELNO
     , FXNO
     , ZIP
     , ADDR
     , SE
     , CLSG_MNG_BRNCH
     , STTS_CD
     , CRT_ID
     , UPD_ID
)
VALUES (
       #{jntCd}
     , #{rprsFnstCd}
     , #{fnstCd}
     , #{fnstNm}
     , #{storNm}
     , #{telno}
     , #{fxno}
     , #{zip}
     , #{addr}
     , #{se}
     , #{clsgMngBrnch}
     , #{sttsCd}
     , #{crtId}
     , #{updId}
)

mapper.update:
UPDATE TB_FNNC_JNT_CD_INFO
   SET RPRS_FNST_CD = #{rprsFnstCd}
     , FNST_CD = #{fnstCd}
     , FNST_NM = #{fnstNm}
     , STOR_NM = #{storNm}
     , TELNO = #{telno}
     , FXNO = #{fxno}
     , ZIP = #{zip}
     , ADDR = #{addr}
     , SE = #{se}
     , CLSG_MNG_BRNCH = #{clsgMngBrnch}
     , STTS_CD = #{sttsCd}
     , UPD_ID = #{updId}
 WHERE JNT_CD = #{jntCd}

mapper.select:
SELECT COUNT(*)
  FROM TB_FNNC_JNT_CD_INFO
 WHERE JNT_CD = #{jntCd}

SELECT JNT_CD
     , RPRS_FNST_CD
     , FNST_CD
     , FNST_NM
     , STOR_NM
     , TELNO
     , FXNO
     , ZIP
     , ADDR
     , SE
     , CLSG_MNG_BRNCH
     , STTS_CD
     , CRT_ID
     , date_format(CRT_DTTM,   '%Y-%m-%d %H:%i:%s') AS CRT_DTTM
     , UPD_ID
     , date_format(UPD_DTTM,   '%Y-%m-%d %H:%i:%s') AS UPD_DTTM
  FROM TB_FNNC_JNT_CD_INFO
 WHERE 1=1

   AND JNT_CD = #{jntCd}


   AND RPRS_FNST_CD = #{rprsFnstCd}


   AND FNST_CD = #{fnstCd}


   AND FNST_NM LIKE CONCAT('%',#{fnstNm} , '%')


   AND STOR_NM LIKE CONCAT('%',#{storNm} , '%')


   AND TELNO = #{telno}


   AND FXNO = #{fxno}


   AND ZIP = #{zip}


   AND ADDR = #{addr}


   AND SE = #{se}


   AND CLSG_MNG_BRNCH = #{clsgMngBrnch}


   AND STTS_CD = #{sttsCd}
